const esPalindromo=t=>{t=t.toLowerCase().replace(/[^a-z0-9]/g,"");for(let i=0;i<t.length/2;i++)if(t[i]!==t[t.length-1-i])return!1;return!0};const verificarPalindromo=()=>{const t=document.getElementById("texto").value;document.getElementById("resultado").innerText=esPalindromo(t)?"Es un palíndromo":"No es un palíndromo"};
